DROP DATABASE IF EXISTS globalproducts;CREATE DATABASE IF NOT EXISTS globalproducts;USE globalproducts;CREATE TABLE region (  region_id INT(11) NOT NULL AUTO_INCREMENT,  PRIMARY KEY(region_id),  name VARCHAR(255),  description VARCHAR(255),  is_active BOOL ) ENGINE=InnoDB  COLLATE utf8_general_ci;  CREATE TABLE format (  format_id INT(11) NOT NULL AUTO_INCREMENT,  PRIMARY KEY(format_id),  name VARCHAR(255),  description VARCHAR(255),  is_active BOOL ) ENGINE=InnoDB  COLLATE utf8_general_ci;    CREATE TABLE brand (  brand_id INT(11) NOT NULL AUTO_INCREMENT,  PRIMARY KEY(brand_id),  name VARCHAR(255),  description VARCHAR(255),  is_active BOOL ) ENGINE=InnoDB  COLLATE utf8_general_ci; CREATE TABLE size (  size_id INT(11) NOT NULL AUTO_INCREMENT,  PRIMARY KEY(size_id),  name VARCHAR(255),  name_metric VARCHAR(255),  description VARCHAR(255),  lower_range INT NOT NULL,  upper_range INT NOT NULL,  is_active BOOL ) ENGINE=InnoDB  COLLATE utf8_general_ci;   CREATE TABLE finish (  finish_id INT(11) NOT NULL AUTO_INCREMENT,  PRIMARY KEY(finish_id),  name VARCHAR(255),  description VARCHAR(255),  is_active BOOL ) ENGINE=InnoDB  COLLATE utf8_general_ci;   CREATE TABLE products (  prod_id INT(11) NOT NULL AUTO_INCREMENT,  PRIMARY KEY(prod_id),  name VARCHAR(255),  code_name VARCHAR(355),  dimension VARCHAR(255),  size INT NOT NULL,  status VARCHAR(255),  description VARCHAR(255),  image_path VARCHAR(355),  image_name VARCHAR(255),  region_id INT NOT NULL,  format_id INT NOT NULL,  brand_id INT NOT NULL,  finish_id INT NOT NULL,  is_active BOOL ) ENGINE=InnoDB  COLLATE utf8_general_ci;    INSERT INTO region (region_id, name, description, is_active) VALUES  (NULL, "N/A","N/A", 1),  (NULL, "Africa","Africa", 1),  (NULL, "Asia","Asia", 1),  (NULL, "Australia","Australia", 1),  (NULL, "Europe","Europe", 1),  (NULL, "North America","North America", 1),  (NULL, "South America","South America", 1);INSERT